// Add Azure App Configuration to the container.
var azAppConfigConnection = @@localVar@@.Configuration[`@@connectionId@@`];
if (!string.IsNullOrEmpty(azAppConfigConnection))
{
    // Use the connection string if it is available.
    @@localVar@@.Configuration.AddAzureAppConfiguration(options =>
    {
        options.Connect(azAppConfigConnection)
        .ConfigureRefresh(refresh =>
        {
            // All configuration values will be refreshed if the sentinel key changes.
            refresh.Register(`TestApp:Settings:Sentinel`, refreshAll: true);
        });
    });
}
else if (Uri.TryCreate(@@localVar@@.Configuration[`Endpoints:@@connectionId@@`], UriKind.Absolute, out var endpoint))
{
    // Use Azure Active Directory authentication.
    // The identity of this app should be assigned 'App Configuration Data Reader' or 'App Configuration Data Owner' role in App Configuration.
    // For more information, please visit https://aka.ms/vs/azure-app-configuration/concept-enable-rbac
    @@localVar@@.Configuration.AddAzureAppConfiguration(options =>
    {
        options.Connect(endpoint, new DefaultAzureCredential())
        .ConfigureRefresh(refresh =>
        {
            // All configuration values will be refreshed if the sentinel key changes.
            refresh.Register(`TestApp:Settings:Sentinel`, refreshAll: true);
        });
    });
}
@@localVar@@.Services.AddAzureAppConfiguration();

